import React from "react";
// import axios from "axios";
class ImageUpload extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      base64Data: null

    };
  }

  componentDidMount( ){
    // console.log('inside did mount')
    let file = this.props.data;

    if (file) {
      const reader = new FileReader();
      reader.onload = this._handleReaderLoaded.bind(this);
      reader.readAsBinaryString(file);
    }
  };

  _handleReaderLoaded = e => {

    let binaryString = e.target.result;
    this.setState({
      base64Data: [btoa(binaryString).toString()]     
    });
    axios
    .post(`https://api.imgbb.com/1/upload?key=198d62c2c2ca10a6d7838497b71d53ba&image=${this.base64Data}`)
    .then(res => {

        console.log(res)

    })
    .catch(err => {
        console.log(err)
    })
    // const options = await {
    //   method: 'POST',
    //   url: `https://api.imgbb.com/1/upload?key=198d62c2c2ca10a6d7838497b71d53ba&image=${file}`,
    // };

