  //         console.log(response)
  //         return response.data
  //     }).catch(function (error) {
  //         console.error(error);
  //         return error
  //     });
  };

  render() {
     const { base64Data } = this.state;
     let resp = []
     resp.push(JSON.stringify(base64Data))

    return resp
  }
}

export default ImageUpload;
