import { useState, useEffect } from "react";
import Recipe from './takeFullRecipe'
function Table(res) {
    const [tableData, setTableData] = useState([])
    useEffect(() => {
        setTableData(res.data);
        console.log(tableData)
    })
    return (
        <>


            <div className="tableCss">
                <table className="table">

                    <thead>
                        <tr>
                            <th scope="col">id</th>
                            <th scope="col">Name recipe</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            tableData ? tableData.map(tableData => <tr>
                                <th key={tableData.id}>{tableData.id}</th>
                                <td>{tableData.title}</td>
                                <td><img src={tableData.image} className="card-img-top" alt="first Recipe" id='imageList' /></td>
                                <td><button type='button' class="btn btn-tertiary" onClick={() => { return <Recipe /> }}>See the full recipe</button></td>
                                <button className="btn btn-primary" onClick={Add}>Add this recipe in your book</button>
                            </tr>)
                                : console.log("error")
                        }
                    </tbody>
                </table>

            </div >
        </>
    )
}


export default Table;
