import FormNew from './hooks/formNew'
// import axios from 'axios'

export const AddNewRecipe = () => {
    const navigate = useNavigate()
    // const [table, setTable] = useState()
    const [Cuisines, setCuisines] =useState(['African'])
    const [Diet, setDiet] =useState([])
    const [Intolerances, setIntolerances] =useState()
    const [Types, setTypes] =useState()

    const SeeRecipes  = event => {
        if(event.target.id === 'Cuisines'){
            setCuisines(event.target.value)

        }else if(event.target.id === 'Diet'){
            setDiet(event.target.value)

        }else if(event.target.id === 'Intolerances'){
            setIntolerances(event.target.value)

        }else if(event.target.id === 'Types'){
            setTypes(event.target.value)
        }
    }
