    // }, [])
    // function addAtDB(id){
    //     axios.post()
    // }
    return (
        <> 
           <FormNew />

            {/* <UseTable data={table} /> */}
            <button onClick={() => navigate(-1)}>Go back</button>
        </>
    )
}
