import { useState, useEffect } from "react";
import React from "react";
import ReactDOM from "react-dom";
import Recipe from './takeFullRecipe'
import Modal from 'react-bootstrap/Modal';
import ModalBody from "react-bootstrap/ModalBody";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalFooter from "react-bootstrap/ModalFooter";
import ModalTitle from "react-bootstrap/ModalTitle";
import "bootstrap/dist/css/bootstrap.min.css";
import RecipesCards from "./Userecipecards";
function Table(res) {
    const [isOpen, setIsOpen] = React.useState(false);

    const showModal = () => {
        setIsOpen(true);
    };

    const hideModal = () => {
        setIsOpen(false);
    };

    const [tableData, setTableData] = useState([])
    useEffect(() => {
        setTableData(res.data);
        // console.log(tableData)

    })
    return (
        <>


            <div className="tableCss">
                <table className="table">

                    <thead>
                        <tr>
                            <th scope="col">id</th>
                            <th scope="col">Name recipe</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            tableData ? tableData.map(tableData => <tr>
                                <th key={tableData.id}>{tableData.id}</th>
                                <td>{tableData.title}</td>
                                <td><img src={tableData.image} className="card-img-top" alt="first Recipe" id='imageList' /></td>
                                <td>

                                    {/* <Recipe data={tableData.id} ></Recipe> */}
                                  <div
      className="modal show"
      style={{ display: 'block', position: 'initial' }}
    >
      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>Modal body text goes here.</p>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary">Close</Button>
          <Button variant="primary">Save changes</Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
                            </td>
                            </tr>
                    )

                    : console.log("error")
                        }
                </tbody>
            </table>

        </div >
        </>
    )
}


export default Table;
