import { useState, useEffect } from "react";
import React from "react";
import ReactDOM from "react-dom";
import Recipe from './takeFullRecipe'
import Modal from 'react-bootstrap/Modal';
import ModalBody from "react-bootstrap/ModalBody";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalFooter from "react-bootstrap/ModalFooter";
import ModalTitle from "react-bootstrap/ModalTitle";
import "bootstrap/dist/css/bootstrap.min.css";
import RecipesCards from "./Userecipecards";
function Table(res) {
    const [isOpen, setIsOpen] = React.useState(false);

    const showModal = () => {

        setIsOpen(true);
    };

    const hideModal = () => {
        setIsOpen(false);
    };
    const [tableData, setTableData] = useState([])
    useEffect(() => {
        setTableData(res);
        console.log(tableData)
    })
    return (
        <>


            <div className="tableCss">
                <table className="table">

                    <thead>
                        <tr>
                            <th scope="col">id</th>
                            <th scope="col">Name recipe</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            tableData ? tableData.map(tableData => <tr>
                                <th key={tableData.id}>{tableData.id}</th>
                                <td>{tableData.title}</td>
                                <td><img src={tableData.image} className="card-img-top" alt="first Recipe" id='imageList' /></td>
                                <td>
                                    <button className="btn btn-primary"  onClick={showModal}>Take a look on the full recipe</button>
                                    <Modal show={isOpen} onHide={hideModal}>
                                        <Modal.Header>
                                            <Modal.Title>Hi</Modal.Title>
                                        </Modal.Header>
                                        <Modal.Body><Recipe data={RecipesCards.id} /></Modal.Body>
                                        <Modal.Footer>
                                            <button className="btn btn-primary"  onClick={hideModal}>Cancel</button>
                                            <button className="btn btn-primary" >Add this recipe in your book</button>
                                        </Modal.Footer>
                                    </Modal>


                            </td>
                            </tr>
                    )

                    : console.log("error")
                        }
                </tbody>
            </table>

        </div >
        </>
    )
}


export default Table;
