      // axios
      // .post(`http:localhost:3001/NewRecipe?title=${this.title}&recipe=${this.recipe}&ingrediente=${this.ingrediente}`)
  }


  render() {
    const { title, recipe, ingrediente, selectedFile } = this.state
    return (

      <div className="App">
        <div className="form-outline mb-4">
          <label className="form-label" htmlFor="typeTitleX-2">
            Title for your recipe:</label><br></br>
          <input type='text' id="typeTitleX-2"
           value={title}
           onChange={this.handleChange} />

        </div>

        <div className="form-outline mb-4">
          {/* <label className="form-label" htmlFor="typeRecipeX-2">Give some details about your recipe</label> */}
          <input type="text" id="typeRecipeX-2" className="form-control form-control-lg" rows="3" 
          value={recipe}
          onChange={this.handleChange}  />
        </div>
        <div className="form-outline mb-4">
          {/* <label className="form-label" htmlFor="typeRecipeX-2">Give some details about your recipe</label> */}
          <input type="text" id="typeIngredienteX-2" className="form-control form-control-lg" rows="3" 
          value={ingrediente}
          onChange={this.handleChange}  />
        </div>
        {this.state.image}

        <input type="file" className="" id=""  file={selectedFile}
          onChange={this.handleChange}  />
        <button onClick={this.handleUpload}>Upload</button>

      </div>
    )
  }
}

export default App;
