import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Spices from '../image/homeImage.png'
import RecipesCards from "./hooks/Userecipecards";
import OpenImages from './hooks/OpenImages'

function HomePage() {
    const [cards, setCards] = useState([])
    useEffect(() => {

    //    setCards( OpenImages('http://localhost:3001/recipesPopular'))

    //     setCards(OpenImages())
    //     console.log(data)
    //     setCards(data)
    //     console.log(cards)
        let map = []
        setCards(map)
        const axiosData = async () => {
          await axios
                .get('http://localhost:3001/recipesPopular')
                .then(res => {
                    setCards(res.data[0])
                    let dat = res.data[0]
                    for (let i in dat) {

                        if (dat[i].formUser == 'true') {
                            // console.log('test')
                            async function fetchData()  {
                            fetch(`http://localhost:3001/files/pexels-kim-van-vuuren-1590139.jpg`)
                                // .then(r => r.arrayBuffer())
                                .then(r => { 
                                    const blob = new Blob([r],image);
                                    const url =  URL.createObjectURL(blob);
                                    dat[i].image =  url
                                    map.push(dat[i])
                                    // console.log(map)
                                });


                            }
                            fetchData()
                        }
                        else {
                          map.push(dat)
                        }
                    }

                })
                .catch(err => {
                    console.log(err)
                })
                // console.log(map)
                // setCards(map)
            }
            axiosData()
            // console.log(cards)
    }, [])

    return (
        <div className='Home'>
            <div>
                <img src={Spices} className="img-fluid" alt="spices" id="image" />
            </div>
            <div className="Popularrecipes">
                <h2>Here are some of the favorite recipes from our users </h2>
            </div>
            <RecipesCards data={cards} />
        </div>
    )




}

export default HomePage
