import axios from 'axios';

import React, { Component } from 'react';

class App extends Component {
  constructor(props){

  }
  handleselectedFile = event => {
    this.setState({
      selectedFile: event.target.files[0],
    })}
    handleselectedTitle = event => {
      this.setState({
        title: event.target.text,
      })
    }
    handleselectedRecipe = event => {
      this.setState({
        recipe: event.target.recipe,
      })
    }
    handleselectedIngredite = event => {
      this.setState({
        ingrediente: event.target.ingrediente
      })
    }

    handleUpload = () => {
      console.log(this.state.title)
      const data = new FormData()
      data.append('file', this.state.selectedFile, this.state.selectedFile.name)
      axios
        .post('http://localhost:3001/upload', data, {
          onUploadProgress: ProgressEvent => {

          },
        })
        .then(res => {
          console.log(res.statusText)
        })
      //   axios
      //   .post(`http:localhost:3001/NewRecipe?title=${this.title}&recipe=${this.recipe}&ingrediente=${this.ingrediente}`)
    }


    render() {

      return (
        <div className="App">
          <div className="form-outline mb-4">
            <label className="form-label" htmlFor="typeTitleX-2">
                    Title for your recipe:</label><br></br>
            <input type='text' id="typeTitleX-2"
              onChange={this.handleselectedTitle} />

          </div>

          <div className="form-outline mb-4">
            {/* <label className="form-label" htmlFor="typeRecipeX-2">Give some details about your recipe</label> */}
            <input type="recipe" id="typeRecipeX-2" className="form-control form-control-lg" rows="3" onChange={this.handleselectedFile} />

          </div>

          <input type="file" className="" id="" onChange={this.handleselectedFile} />
          <button onClick={this.handleUpload}>Upload</button>

        </div>
      )
    }
  }

      export default App;
