import { useState, useEffect } from "react";
import React from "react";
import ReactDOM from "react-dom";
import Recipe from './takeFullRecipe'
import Modal from 'react-bootstrap/Modal';
import ModalBody from "react-bootstrap/ModalBody";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalFooter from "react-bootstrap/ModalFooter";
import ModalTitle from "react-bootstrap/ModalTitle";
import "bootstrap/dist/css/bootstrap.min.css";
import RecipesCards from "./Userecipecards";
function Table(res) {
    // const [isOpen, setIsOpen] = React.useState(false);

    // const showModal = () => {
    //     setIsOpen(true);
    // };

    // const hideModal = () => {
    //     setIsOpen(false);
    // };
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [tableData, setTableData] = useState([])
    useEffect(() => {
        setTableData(res.data);
        // console.log(tableData)

    })
    return (
        <>


            <div className="tableCss">
                <table className="table">

                    <thead>
                        <tr>
                            <th scope="col">id</th>
                            <th scope="col">Name recipe</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            tableData ? tableData.map(tableData => <tr>
                                <th key={tableData.id}>{tableData.id}</th>
                                <td>{tableData.title}</td>
                                <td><img src={tableData.image} className="card-img-top" alt="first Recipe" id='imageList' /></td>
                                <td>

                                    {/* <Recipe data={tableData.id} ></Recipe> */}
                                    <button variant="primary" onClick={handleShow}>
                                        Launch demo modal
                                    </button>
                                    <Modal show={show} onHide={handleClose}>
                                        <Modal.Header closeButton>
                                            <Modal.Title>{tableData.title}</Modal.Title>
                                        </Modal.Header>
                                        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                                        <Modal.Footer>
                                            <button variant="secondary" onClick={handleClose}>
                                                Close
                                            </button>
                                            <button className="btn btn-primary" variant="primary" onClick={handleClose}>
                                                Save Changes
                                            </button>
                                        </Modal.Footer>
                                    </Modal>
                                </td>
                            </tr>
                            )

                                : console.log("error")
                        }
                    </tbody>
                </table>

            </div >
        </>
    )
}


export default Table;
