import axios from 'axios';

import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      title: '',
      recipe: '',
      ingrediente: '',
      selectedFile:'',
      selectedPath:'',
      image:''
    }
  }
  handleChange = event => {
    if (event.target.type === 'file') {
      this.setState({
        selectedFile: event.target.files[0],
      })
    }
    else if(event.target.id === 'typeTitleX-2'){
      this.setState({
        title: event.target.value,
      })
    }
    else if(event.target.id === 'typeRecipeX-2'){
      this.setState({
        recipe: event.target.value,
      })
    }
    else{
      this.setState({
        ingrediente: event.target.value
      })
    }

  }

  handleUpload = () => {
    console.log(this.state.title)
    const data = new FormData()
    data.append('file', this.state.selectedFile, this.state.selectedFile.name)
    this.setState({
      selectedPath: this.state.selectedFile.name
    })
    console.log(this.state.selectedFile.name)

    // axios
    //   .post('http://localhost:3001/upload', data, {
    //     onUploadProgress: ProgressEvent => {

    //     },
    //   })
    //   .then(res => {
    //     console.log(res.statusText)
    //   })
      // axios
      // .post(`http:localhost:3001/NewRecipe?title=${this.title}&recipe=${this.recipe}&ingrediente=${this.ingrediente}`)
  }


  render() {
    const { title, recipe, ingrediente, selectedFile } = this.state
    return (

      <div className="App">
        <div className="form-outline mb-4">
          <label className="form-label" htmlFor="typeTitleX-2">
            Title for your recipe:</label><br></br>
          <input type='text' id="typeTitleX-2"
           value={title}
           onChange={this.handleChange} />

        </div>

        <div className="form-outline mb-4">
          {/* <label className="form-label" htmlFor="typeRecipeX-2">Give some details about your recipe</label> */}
          <input type="text" id="typeRecipeX-2" className="form-control form-control-lg" rows="3" 
          value={recipe}
          onChange={this.handleChange}  />
        </div>
        <div className="form-outline mb-4">
          {/* <label className="form-label" htmlFor="typeRecipeX-2">Give some details about your recipe</label> */}
          <input type="text" id="typeIngredienteX-2" className="form-control form-control-lg" rows="3" 
          value={ingrediente}
          onChange={this.handleChange}  />
        </div>
        {this.state.image}

        <input type="file" className="" id=""  file={selectedFile}
          onChange={this.handleChange}  />
        <button onClick={this.handleUpload}>Upload</button>

      </div>
    )
  }
}

export default App;
