import React, { useState, useEffect } from 'react'

import axios from "axios";
import Button from 'react-bootstrap/Button';
// import Modal from 'react-bootstrap/Modal';
// import e from 'express';

function FullRecipe(data) {
    const id = data.data;
    const [content, setContent] = useState([<div dangerouslySetInnerHTML={{ __html: id.ingredients }} />])
    function Reci() {
        setContent(
            <div dangerouslySetInnerHTML={{ __html: id.recipe }} />
        )
    }
    function Ingre() {
        setContent(
            <div dangerouslySetInnerHTML={{ __html: id.ingredients }} />
        )
    }

    return (
        <>
            {content}
            <button id='bu1' className="btn btn-primary btn-lg btn-block"  onClick={()=>{Reci()}} >Click to see the recipe instructions</button>
            <button id='bu1' className="btn btn-primary btn-lg btn-block"  onClick={()=>{Ingre()}}>Click to see the recipe ingredients</button>




        </>
    )
}
export default FullRecipe
