import { useState, useEffect } from "react";

function Table(data){
    const [tableData, setTableData] = useState([])
    const [row, setRow] = useState(0)
    const [page, setPage] = useState(0)


    useEffect(() => {
        for(row < (row+4); row++; ) {
        setTableData(current =>[...current, data[row].title]);
        }
    }, [page])
    function handleClick(){
        setPage(page +1)
    }
    return(
        <div>
        <p>{tableData}</p>
        <button onClick={handleClick}>see recipes</button>
        </div>
    )
}


export default Table;
