import { useState, useEffect } from "react";
import ReactDOM from "react-dom";
// import Recipe from './takeFullRecipe'
import Modal from 'react-bootstrap/Modal';
import ModalBody from "react-bootstrap/ModalBody";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalFooter from "react-bootstrap/ModalFooter";
import ModalTitle from "react-bootstrap/ModalTitle";

function modal(data) {

    for (let i in data.data[0]) {
        const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
        console.log(data.data[0].i)
        return (

            <>
                <button className="btn btn-primary" variant="primary" onClick={handleShow}>
                    modal
                </button>
                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>some</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>test {i}</Modal.Body>
                    <Modal.Footer>
                        <button className="btn btn-primary" variant="secondary" onClick={handleClose}>
                            Close
                        </button>
                        <button className="btn btn-primary" variant="primary" onClick={handleClose}>
                            Add this recipe
                        </button>
                    </Modal.Footer>
                </Modal>
            </>
        )
    }
}
export default modal
