    // console.log(id)
    useEffect(() => {
        console.log('inside useEffect Popular recipes')

        axios
            .get(`http://localhost:3001/getFullRecipes?id=${id}`)
            .then(res => {
                setRecipe(res.data)
                console.log(recipe)
