        super()
        this.state ={
            Cuisines: '',
            Diet: '',
            Intolerances: '',
            Types: ''
        }
        handle = event => {
            if(event.target.id === 'Cuisines'){
                this.setState({
                    Cuisines: event.target.value
                })
            }else if(event.target.type === 'Diet'){
                this.setState({
                    Diet: event.target.value
                        })
            }else if(event.target.type === 'textarea'){
                this.setState({
                            comments: event.target.value
                        })
            }else if(event.target.type === 'textarea'){
                this.setState({
                            comments: event.target.value
                        })
            }
        }
    }
}

