import React, { useState, useEffect } from 'react'
import Recipe from './takeFullRecipe'
import AddBook from './AddBook.js'
function RecipesCards(res) {

    const [cards, setCards] = useState([" "])
    useEffect(() => {
        setCards(res.data);
        console.log(cards)
    })
function Add(id){
    return <AddBook data={id} />
}
    return (
        <div className='popular'>
            <div id='Card' className="row row-cols-1 row-cols-md-4 g-3 mx-5 p-4 mb-4">
                {
                    cards ? cards.map(cards =>
                        <div id='cards'className="card-group" key={cards.id}>
                            <div id='cardbody' className="card shadow-lg" >
                                <img id='cardimage' src={cards.image} className="card-img-top" alt="first Recipe" />
                                <div className="card-body ">
                                    <h5 className="card-title">{cards.title}</h5>
                                    <button className="btn btn-primary mt-4" onClick={() => <Recipe data={cards.id} />}>See the full recipe</button><br></br>
                                    <button className="btn btn-primary" onClick={Add}>Add this recipe in your book</button>
                                </div>
                            </div>
                        </div>)
                        : console.log("error")
                }
            </div>
        </div>
    )

}
export default RecipesCards
