import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Spices from '../image/homeImage.png'
import RecipesCards from "./hooks/Userecipecards";

function HomePage() {
    const [cards, setCards] = useState()
    useEffect(() => {

        axios
            .get('http://localhost:3001/recipesPopular')
            .then(res => {

                // setCards(res.data[0])
                // console.log(res.data[0][1])
                // console.log(cards[1])
                let dat = res.data[0]
                for(let i in dat){
                    if(da[i].formUser==='true'){
                         fetch( `http://localhost:3001/files/cbimage(1).png`)
                    .then( r => r.arrayBuffer() )
                    .then( buffer => { // note this is already an ArrayBuffer
                      // there is no buffer.data here

                      const blob = new Blob( [ buffer ] );
                    //   console.log(blob)
                      const url = URL.createObjectURL( blob );
                      dat[i].image=url
                      setCards(cards => [...cards, dat[i]])
                    //   console.log(cards[i].image)
                    } );
                    }
                    else{
                        setCards(cards => [...cards, dat[i]])
                    }
                }

            })
            .catch(err => {
                console.log(err)
            })
            // fetch( 'http://localhost:3001/files/PropsDisplayer.png' )
            // .then( r => r.arrayBuffer() )
            // .then( buffer => { // note this is already an ArrayBuffer
            //   // there is no buffer.data here

            //   const blob = new Blob( [ buffer ] );
            //   console.log(blob)
            //   const url = URL.createObjectURL( blob );
            //   const img = document.getElementById( 'img' );
            //   img.src = url;
            //   // So the Blob can be Garbage Collected
            //   img.onload = e => URL.revokeObjectURL( url );
            //   // ... do something else with 'buffer'
            // } );

        }, [])

    return (
        <div className='Home'>
            {/* <img id="img"></img> */}
            <div>
                <img src={Spices} className="img-fluid" alt="spices" id="image" />
            </div>
            <div className="Popularrecipes">
                <h2>Here are some of the favorite recipes from our users </h2>
            </div>
            <RecipesCards data={cards} />
        </div>
    )




}

export default HomePage
