import React, { useState, useEffect } from 'react'
import axios from "axios";

export function fullRecipe (id){
    const [recipe, setRecipe] =useState([' '])
    useEffect(() => {
        console.log('inside useEffect Popular recipes')

        axios
        .get(`http://localhost:3001/getFullRecipes?id=${id}`)
            .then(res => {
                // console.log(res)
                setRecipe(res.data[0])
            })
            .catch(err => {
                console.log(err)
            })
    }, [])
    return(
        <p>{recipe.instructions}</p>
    )
}
