                                    dat[i].image =  '	http://localhost:3000/Users/WillyErlemann/Documents/coding/Capstone-Project/backEnd/images/cbimage(1).png'
                                    map.push(dat[i])
                            //         console.log(map)
                            //     });


                            // }
                            // fetchData()
                        }
                        else {
                          map.push(dat[i])
                        }
                    }

                })
                .catch(err => {
                    console.log(err)
                })
                // console.log(map)
                setCards(map)
            }
            axiosData()
            console.log(cards)
    }, [])

    return (
        <div className='Home'>
            <div>
                <img src={Spices} className="img-fluid" alt="spices" id="image" />
            </div>
            <div className="Popularrecipes">
                <h2>Here are some of the favorite recipes from our users </h2>
            </div>
            <RecipesCards data={cards} />
        </div>
    )




}

export default HomePage
