import React, { useState, useEffect } from "react";
import UseTable from "./hooks/useTable";
import Blob from 'fetch-blob'
import axios from 'axios'
function Popular() {
    const [table, setTable] = useState()
    useEffect(() => {
        // console.log('inside useEffect Popular recipes')
        axios
            .get('http://localhost:3001/savedRecipes')
            .then(res => {
                console.log(res.data)
                let respo = res.data[0]
                for(let i in respo){

                if(respo[i].formUser=='true'){
                    fetch('http://localhost:3001/files/cbimage(1).png')
                    .then(r => r.blob()) // consume as a Blob
                    .then(blob => {
                        const url = URL.createObjectURL(blob);
                        const img = document.getElementById(respo[i].id);
                        img.src = url;
                        // in case you don't need the blob anymore
                        img.onload = e => URL.revokeObjectURL(url);

                    });
                    // respo[i].image='2'
                }

            }
                // console.log(res.data)
                setTable(respo)
            })
            .catch(err => {
                // console.log(err)
            })
            // console.log(table)
            //     console.log(res)
            //     setTable(res.data[0])
            // })
            // .catch(err => {
            //     console.log(err)
            // })
    }, [])


    return (
        <div className="PopularPage">
               <div className="Popularrecipes">
                <h2>Here are some of the favorite recipes from our users </h2>
            </div>
        <UseTable data={table} />

        </div>
    )
}
export default Popular







