import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Spices from '../image/homeImage.png'
import RecipesCards from "./hooks/Userecipecards";

function HomePage() {
    const [cards, setCards] = useState([''])


    useEffect(() => {
        let map = []
            axios
                .get('http://localhost:3001/recipesPopular')
                .then(res => {
                    let dat = res.data[0]
                    for (let i in dat) {
                        if (dat[i].formUser === 'true') {
                            fetch(`http://localhost:3001/files/cbimage(1).png`)
                                .then(r => r.arrayBuffer())
                                .then(buffer => { 
                                    const blob = new Blob([buffer]);
                                    const url = URL.createObjectURL(blob);
                                    dat[i].image = url
                                    // setCards(cards => [...cards, dat[i]])
                                    map.push(dat[i])
                                });
                                let j = runs + 1;
                                setRuns(j)
                                console.log(j)
                        }
                        else {
                            let j = runs + 1;
                            setRuns(j)
                            setCards(cards => [...cards, dat[i]])
                            console.log(j)
                        }
                    }
                })
                .catch(err => {
                    console.log(err)
                })
    }, [])

    return (
        <div className='Home'>
            <div>
                <img src={Spices} className="img-fluid" alt="spices" id="image" />
            </div>
            <div className="Popularrecipes">
                <h2>Here are some of the favorite recipes from our users </h2>
            </div>
            <RecipesCards data={cards} />
        </div>
    )




}

export default HomePage
