                                    map.push(dat[i])
                                    console.log(map)
                                });


                            }
                            fetchData()
                        }
                        else {
                          map.push(dat)
                        }
                    }

                })
                .catch(err => {
                    console.log(err)
                })
                console.log(map)
                setCards(map)
            // }
            // axiosData()
            console.log(cards)

    return {data:cards}
}
export default OpenImages
